import React, { useEffect, useState } from "react";
import "../../styles/Home/Testimonials.css";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/navigation";

function Testimonials() {
  const [swiperRef, setSwiperRef] = useState(null);
  const [activeIndex, setActiveIndex] = useState(1);
  const [slidesPerView, setSlidesPerView] = useState(2.7);

  const handleSlideChange = () => {
    if (swiperRef) {
      setActiveIndex(swiperRef.activeIndex);
    }
  };
  useEffect(() => {
    const handleResize = () => {
      const screenWidth = window.innerWidth;
      if (screenWidth <= 500) {
        setSlidesPerView(1.2);
      } else if (screenWidth <= 900) {
        setSlidesPerView(1.7);
      } else {
        setSlidesPerView(2.7);
      }
    };

    handleResize();
    window.addEventListener("resize", handleResize); 
    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, [])

  const images = [
    {
      user: "data:image/jpeg;base64,",
      message:
        "As a production supervisor, I rely on suppliers who can consistently deliver high-quality materials on time. Rakarni has been our go-to supplier for gypsum powder for years, and they've never let us down. Their product quality is top- notch, and their commitment to customer satisfaction is commendable. It's been a pleasure working with them.",
      name: "Ramesh Kumar",
      proffetion: "Constrution Contractor",
    },
    {
      //   src: image1,
      user: "data:image/jpeg;base64,",
      message:
        "I've been using gypsum powder from Rakarni for my construction projects for years, and I'm always impressed by its quality and consistency. It's easy to work with and provides excellent binding properties, ensuring strong and durable structures. Highly recommend it to fellow contractors.",
      name: "Deepak Verma",
      proffetion: "Production Supervisor",
    },
    {
      //   src: image1,
      user: "data:image/jpeg;base64,",
      message:
        "Our manufacturing processes demand high-quality materials, and Rakarni's gypsum powder never disappoints. It's consistently pure and finely ground, making it ideal for our production of gypsum-based products. The customized solutions they offer perfectly align with our needs, making them our trusted partner in manufacturing.",
      name: "Rajesh Singh",
      proffetion: "Manufacturing Manager",
    },
  ];

  return (
    <>
      <div className="testimonail_main_div">
        <div 
         data-aos="zoom-in"
         data-aos-offset="200"
         data-aos-delay="100"
         data-aos-duration="500"
        >
          <h3 className="heading-text-testimonial text-center">Testimonials</h3>
        </div>
        <div className="our-product-slider-start ">
          {/* big screen */}
          {/* <div className="testimonail_for_big_screen"> */}
          <Swiper
            onSwiper={setSwiperRef}
            onSlideChange={handleSlideChange}
            slidesPerView={slidesPerView}
            initialSlide={1}
            centeredSlides={true}
            spaceBetween={20}
            pagination={{
              type: "fraction",
            }}
            className="mySwiper"
          >
            {images.map((testimonial, index) => (
              <SwiperSlide key={index} style={{ position: "relative" }}>
                <div
                  className="testimonail_card_main"
                  style={{
                    transform: `scale(${activeIndex === index ? 1 : 0.8})`,
                    transition: "transform 0.3s ease-in-out",
                  }}
                >
                  {/* top */}
                  <div className="testimmonial_card_top">
                    {/* image */}
                    <div className="testimmonial_card_top_image_section">
                      <img
                        src={testimonial.user}
                        alt=""
                        srcset=""
                        className="user_image_testimonoal"
                      />
                    </div>
                    {/* content */}
                    <div className="testimmonial_card_top_details_section">
                      <div>{testimonial.name},</div>
                      <div>{testimonial.proffetion}</div>
                    </div>
                  </div>
                  {/* bootom */}
                  <div className="testimmonial_card_bottom">
                    <div className="testimmonial_card_message">
                      {testimonial.message}
                    </div>
                  </div>
                </div>
              </SwiperSlide>
            ))}
          </Swiper>
        </div>
      </div>
    </>
  );
}

export default Testimonials;
